	(1000, 1, '2013-03-15 14:48:32', '2013-03-15 19:48:32', 'Nested and mixed lists are an interesting beast. It\'s a corner case to make sure that\n<ul>\n	<li>Lists within lists do not break the ordered list numbering order</li>\n	<li>Your list styles go deep enough</li>\n</ul>\n<h3>Ordered - Unordered - Ordered</h3>\n<ol>\n	<li>ordered item</li>\n	<li>ordered item\n<ul>\n	<li><strong>unordered</strong></li>\n	<li><strong>unordered</strong>\n<ol>\n	<li>ordered item</li>\n	<li>ordered item</li>\n</ol>\n</li>\n</ul>\n</li>\n	<li>ordered item</li>\n	<li>ordered item</li>\n</ol>\n<h3>Ordered - Unordered - Unordered</h3>\n<ol>\n	<li>ordered item</li>\n	<li>ordered item\n<ul>\n	<li><strong>unordered</strong></li>\n	<li><strong>unordered</strong>\n<ul>\n	<li>unordered item</li>\n	<li>unordered item</li>\n</ul>\n</li>\n</ul>\n</li>\n	<li>ordered item</li>\n	<li>ordered item</li>\n</ol>\n<h3>Unordered - Ordered - Unordered</h3>\n<ul>\n	<li>unordered item</li>\n	<li>unordered item\n<ol>\n	<li>ordered</li>\n	<li>ordered\n<ul>\n	<li>unordered item</li>\n	<li>unordered item</li>\n</ul>\n</li>\n</ol>\n</li>\n	<li>unordered item</li>\n	<li>unordered item</li>\n</ul>\n<h3>Unordered - Unordered - Ordered</h3>\n<ul>\n	<li>unordered item</li>\n	<li>unordered item\n<ul>\n	<li>unordered</li>\n	<li>unordered\n<ol>\n	<li><strong>ordered item</strong></li>\n	<li><strong>ordered item</strong></li>\n</ol>\n</li>\n</ul>\n</li>\n	<li>unordered item</li>\n	<li>unordered item</li>\n</ul>', 'Nested And Mixed Lists', '', 'trash', 'open', 'open', '', 'nested-and-mixed-lists__trashed', '', '', '2016-12-08 03:26:55', '2016-12-08 03:26:55', '', 0, 'http://wptest.io/demo/?p=1000', 0, 'post', '', 0),
	(1005, 1, '2012-12-02 03:00:34', '2012-12-02 09:00:34', '[wpvideo tFnqC9XQ w=680]\n\n<a title="VideoPress Plugin for WordPress" href="http://videopress.com/" target="_blank">VideoPress</a>, especially as a video post format, usually provides some unique styling issues.\n\nYou will need to install <a title="Jetpack for WordPress" href="http://jetpack.me/" target="_blank">Jetpack</a> or <a title="Slim Jetpack" href="http://wordpress.org/extend/plugins/slimjetpack/" target="_blank">Slim Jetpack</a> plugin to turn the shortcode into a viewable video.', 'Post Format: Video (VideoPress)', '', 'publish', 'open', 'open', '', 'post-format-video-videopress', '', '', '2012-12-02 03:00:34', '2012-12-02 09:00:34', '', 0, 'http://wptest.io/demo/?p=1005', 0, 'post', '', 0),
